import React from 'react';

import { userService } from '../_services';

class LoginPage extends React.Component {
    constructor(props) {
        super(props);

        userService.logout();

        this.state = {
            username: '',
            password: '',
            cpassword:'',
            emailid:'',
            submitted: false,
            loading: false,
            loading1: false,
            error: '',
            page:'login'
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleSignUp = this.handleSignUp.bind(this);
        this.handleSignupSubmit = this.handleSignupSubmit.bind(this);
    }

    handleChange(e) {
        const { name, value } = e.target;
        this.setState({ [name]: value });
    }
    handleSignUp(e) {
        e.preventDefault
        this.setState({
            page:'',
            error:'',
            submitted:false,
            username:'',
            password:'',
            cpassword:'',
            loading:false,
            loading1:true,
            emailid:''
        })

    }
    validateEmail()
    { const{emailid}=this.state;
        var e= /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
        return e.test(emailid);
    }
    handleSignupSubmit(e){
         e.preventDefault();
        this.setState({ submitted: true });
        const{username,password,cpassword,emailid,error}=this.state;
        // stop here if form is invalid
        if (!(username && password && cpassword && emailid && (password==cpassword) && this.validateEmail())) {
            return ;
        }
            this.setState({ submitted:true,loading: true,page:'login', });
            alert("User "+ username +' is added in the database ')
            this.setState({loading:false,loading1:false,submitted:false,username:'',password:''})
         
      
    }

    handleSubmit(e) {
        e.preventDefault();

        this.setState({ submitted: true });
        const { username, password, returnUrl } = this.state;

        // stop here if form is invalid
        if (!(username && password)) {
         
            return;
        }

        this.setState({ loading:true,loading1:false });
        userService.login(username, password)
            .then(
                user => {
                    const { from } = this.props.location.state || { from: { pathname: "/" } };
                    this.props.history.push(from);
                },
                error => this.setState({ error, loading: false })
            );
    }

    render() {
        const { username, password, submitted, loading, error,page,cpassword,emailid,loading1} = this.state;
        return ( 
            (page=='login')?   
            <div className="col-md-6 col-md-offset-3">
                <div className="alert alert-info">
                    Username: test<br />
                    Password: kiprosh
                </div>
                <h2 align="center">Login</h2>
                <form name="form">
                    <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                        <label htmlFor="username">Username</label>
                        <input type="text" className="form-control" name="username" value={username} onChange={this.handleChange} />
                        {submitted && !username &&
                            <div className="help-block">Username is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary" disabled={loading} onClick={this.handleSubmit}>Login</button>
                        {loading &&
                            <img style={{marginLeft:-35}} src="data:image/gif;base64," />
                        }
                   
                        <button className="btn btn-primary" style={{marginLeft:190}} disabled={loading1} onClick={this.handleSignUp}>Sign Up</button>
                        {loading1 &&
                            <img src="data:image/gif;base64," />
                        }
                    </div>
                    {error &&
                        <div className={'alert alert-danger'}>{error}</div>
                    }
                </form>
            </div>
            :
            <div className="col-md-6 col-md-offset-3">
                <h2 align="center">Sign up</h2>
                <form name="form" onSubmit={this.handleSignupSubmit}>
                    <div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                        <label htmlFor="username">Username</label>
                        <input type="text" className="form-control" name="username" value={username} onChange={this.handleChange} />
                        {submitted && !username &&
                            <div className="help-block">Username is required</div>
                        }
                    </div>
                    <div className={'form-group' + ((submitted && !emailid)||(submitted && !(this.validateEmail())) ? ' has-error' : '')}>
                        <label htmlFor="username">Email Id</label>
                        <input type="text" className="form-control" name="emailid" value={emailid} onChange={this.handleChange} />
                        {submitted && !emailid &&
                            <div className="help-block">Email Id is required</div>
                            ||
                            submitted && !(this.validateEmail()) &&
                            <div className="help-block">Enter Proper Email Id</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={this.handleChange} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className={'form-group' + ((submitted && !cpassword )||(submitted && (password!==cpassword))? ' has-error' : '')}>
                        <label htmlFor="password"> Confirm Password</label>
                        <input type="password" className="form-control" name="cpassword" value={cpassword} onChange={this.handleChange} />
                        {submitted && !cpassword &&
                            <div className="help-block">Confirm Password is required</div>
                          ||
                            submitted && (password!==cpassword) &&
                            <div className="help-block">Passwords are not same</div> 
                        }
                    </div>
                    <div className="form-group">
                        <button className="btn btn-primary" style={{marginLeft:130}} disabled={loading} onClick={this.handleSignupSubmit}>Register</button>
                        {loading &&
                            <img src="data:image/gif;base64," />
                        }
                    </div>
                    {error &&
                        <div className={'alert alert-danger'}>{error}</div>
                    }
                </form>
            </div>
        );
    }
}

export { LoginPage }; 